import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

import bouclesAgateBleue from '../images/boucles-agate-bleue.jpg';
import bouclesAigueMarine from '../images/boucles-aigue-marine.jpg';
import bouclesApatite from '../images/boucles-apatite.jpg';
import bouclesSablier from '../images/boucles-sablier.jpg';
import bouclesTurquoise from '../images/boucles-turquoise.jpg';
import collierAngelique from '../images/collier-angelique.jpg';
import collierApatie from '../images/collier-apatite.jpg';
import collierDonutMacrame from '../images/collier-donut-macrame.jpg';
import collierEcussonJaspePaysage from '../images/collier-ecusson-jaspe-paysage.jpg';
import collierLapisLazuli from '../images/collier-lapis-lazuli.jpg';
import collierMalachitePetales from '../images/collier-malachite-petales.jpg';
import collierMandalaEstampe from '../images/collier-mandala-estampe.jpg';
import collierSodalite from '../images/collier-sodalite.jpg';
import manchetteApatie from '../images/manchette-apatie.jpg';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  color: #14453a;
  margin-bottom: 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 10px;
  font-size: 16px;
  margin-bottom: 20px;
`;

const CatalogGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 20px;

  @media (min-width: 3840px) {
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr)); /* Ajustez la largeur des colonnes pour les écrans 4K */
  }
`;

const CatalogItem = styled.div`
  background: linear-gradient(
    135deg,
    #14453a 0%, /* Vert */
    #b19e63 100% /* Or */
  );

  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 2px 4px rgba(1, 34, 0, 0.5), 0 -2px 4px rgba(0, 0, 34, 0.5);
  text-align: center;
  cursor: pointer; /* Ajoutez un curseur pointer pour indiquer que les images sont cliquables */

  @media (min-width: 3840px) {
    padding: 40px; /* Ajustez le rembourrage pour les écrans 4K */
  }
`;

const JewelryImage = styled.img`
  max-width: 100%;
  max-height: 100%;

  @media (min-width: 3840px) {
    max-width: 90%; /* Ajustez la largeur maximale de l'image pour les écrans 4K */
    max-height: auto;
  }
`;

const Price = styled.p`
  font-size: 20px;
  color: #14453a;
  margin-bottom: 10px;

  @media (min-width: 3840px) {
    font-size: 30px; /* Ajustez la taille de la police pour les écrans 4K */
  }
`;

const Description = styled.p`
  font-size: 16px;
  color: #333;
  margin-bottom: 20px;

  @media (min-width: 3840px) {
    font-size: 20px; /* Ajustez la taille de la police pour les écrans 4K */
  }
`;

// Style pour la modal
const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  z-index: 1000;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background-color: white;
  padding: 20px;
  max-width: 90%;
  max-height: 90%;
  top: 125px;
  overflow: auto;
  position: fixed;
  border-radius: 10px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
`;

const CloseButton = styled.span`
  position: absolute;
  top: 10px;
  right: 10px;
  cursor: pointer;
  font-size: 32px;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 50%;
  padding: 4px 10px;
`;

// Composant de la modal
const ProductModal = ({ image, title, onClose }) => {
  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <img src={image} alt={title} />
      </ModalContent>
    </ModalOverlay>
  );
};

const Catalog = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    document.title = 'Catalogue - Gemme le fil'; // Mettez le titre souhaité ici
  }, []);

  if (isModalOpen) {
    document.body.style.overflow = 'hidden';
  } else {
    document.body.style.overflow = 'auto';
  }

  const products = [
    {
      image: produit1Image,
      title: 'Produit 1',
      price: '10 €',
      description: 'Description du produit 1.',
    },
    {
      image: produit2Image,
      title: 'Produit 2',
      price: '15 €',
      description: 'Description du produit 2.',
    },
    {
      image: produit3Image,
      title: 'Produit 3',
      price: '20 €',
      description: 'Description du produit 3.',
    },
    {
      image: produit4Image,
      title: 'Produit 4',
      price: '25 €',
      description: 'Description du produit 4.',
    },
    {
      image: bouclesAgateBleue,
      title: 'Boucles Agate Bleue',
      price: '18 €',
      description: 'Des boucles d\'oreilles en agate bleue pour un look élégant.',
    },
    {
      image: bouclesAigueMarine,
      title: 'Boucles Aigue-Marine',
      price: '22 €',
      description: 'Des boucles d\'oreilles en aigue-marine pour un look raffiné.',
    },
    {
      image: bouclesApatite,
      title: 'Boucles Apatite',
      price: '20 €',
      description: 'Des boucles d\'oreilles en apatite pour un style audacieux.',
    },
    {
      image: bouclesSablier,
      title: 'Boucles Sablier',
      price: '25 €',
      description: 'Des boucles d\'oreilles en forme de sablier pour un look unique.',
    },
    {
      image: bouclesTurquoise,
      title: 'Boucles Turquoise',
      price: '18 €',
      description: 'Des boucles d\'oreilles en turquoise pour une touche de couleur.',
    },
    {
      image: collierAngelique,
      title: 'Collier Angelique',
      price: '30 €',
      description: 'Un collier angelique pour une allure angélique.',
    },
    {
      image: collierApatie,
      title: 'Collier Apatie',
      price: '35 €',
      description: 'Un collier en apatite pour un style élégant.',
    },
    {
      image: collierDonutMacrame,
      title: 'Collier Donut Macramé',
      price: '28 €',
      description: 'Un collier avec un pendentif en forme de donut en macramé.',
    },
    {
      image: collierEcussonJaspePaysage,
      title: 'Collier Écusson Jaspe Paysage',
      price: '33 €',
      description: 'Un collier avec un écusson en jaspe paysage pour un look naturel.',
    },
    {
      image: collierLapisLazuli,
      title: 'Collier Lapis Lazuli',
      price: '40 €',
      description: 'Un collier en lapis lazuli pour apporter une touche de bleu à votre style.',
    },
    {
      image: collierMalachitePetales,
      title: 'Collier Malachite et Pétales',
      price: '39 €',
      description: 'Un collier avec des pierres de malachite en forme de pétales.',
    },
    {
      image: collierMandalaEstampe,
      title: 'Collier Mandala et Estampe',
      price: '31 €',
      description: 'Un collier avec un mandala et une estampe pour une touche spirituelle.',
    },
    {
      image: collierSodalite,
      title: 'Collier Sodalite',
      price: '45 €',
      description: 'Un collier en sodalite pour une énergie apaisante.',
    },
    {
      image: manchetteApatie,
      title: 'Manchette Apatie',
      price: '37 €',
      description: 'Une manchette en apatite pour un style audacieux.',
    },
  ];

  const [searchValue, setSearchValue] = useState('');
  const [filteredProducts, setFilteredProducts] = useState(products);

  const handleSearchChange = (event) => {
    const { value } = event.target;
    setSearchValue(value);
  };

  useEffect(() => {
    const filtered = products.filter((product) =>
      product.title.toLowerCase().includes(searchValue.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchValue, products]);

  const openModal = (product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  };

  const generateCatalogItems = () => {
    return filteredProducts.map((product, index) => (
      <CatalogItem key={index} onClick={() => openModal(product)}>
        <JewelryImage src={product.image} alt={product.title} />
        <Title>{product.title}</Title>
        <Price>{product.price}</Price>
        <Description>{product.description}</Description>
      </CatalogItem>
    ));
  };

  return (
    <Container className="custom-container">
      <Title className="title">Réalisations maison</Title>
      <SearchInput
        type="text"
        placeholder="Rechercher un produit..."
        value={searchValue}
        onChange={handleSearchChange}
      />
      <CatalogGrid>{generateCatalogItems()}</CatalogGrid>

      {isModalOpen && selectedProduct && (
        <ProductModal
          image={selectedProduct.image}
          title={selectedProduct.title}
          onClose={closeModal}
        />
      )}
    </Container>
  );
};

export default Catalog;
