import React, { useEffect } from 'react';
import styled from 'styled-components';

// Importer les images des produits depuis le dossier assets/produits
import produit1Image from '../assets/produits/produit0001.png';
import produit2Image from '../assets/produits/produit0002.jpg';
import produit3Image from '../assets/produits/produit0003.png';
import produit4Image from '../assets/produits/produit0004.png';

const CenteredContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Container = styled.div`
  text-align: center;
  position: relative;
  transform: none;
  left: 0;
  top: 1900px;
  background: linear-gradient(135deg, #ffddb0 0%, #1f3a2b 90%);
`;

const Title = styled.h1`
  font-size: 28px;
  color: black; /* Or */
  margin-bottom: 20px;
  border: 1px solid black;
`;

const CatalogGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const CatalogItem = styled.div`
  background: linear-gradient(135deg, #ffddb0 0%, #ffe1d8 100%);
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 2px 4px rgba(1, 34, 0, 0.5), 0 -2px 4px rgba(0, 0, 34, 0.5);
  text-align: center;
`;

const JewelryImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const Price = styled.p`
  font-size: 20px;
  color: #14453A; /* Vert */
  margin-bottom: 10px;
`;

const Description = styled.p`
  font-size: 16px;
  color: #333; /* Noir */
  margin-bottom: 20px;
`;

const Catalog = () => {
  useEffect(() => {
    document.title = 'Catalogue - ISS09 Informatiques saverdun';
  }, []);

  const products = [
    {
      image: produit1Image,
      title: 'Product 1',
      price: '$99.99',
      description: 'Description of product 1',
    },
    {
      image: produit2Image,
      title: 'Product 2',
      price: '$99.99',
      description: 'Description of product 2',
    },
    {
      image: produit3Image,
      title: 'Product 3',
      price: '$99.99',
      description: 'Description of product 3',
    },
    {
      image: produit4Image,
      title: 'Product 4',
      price: '$99.99',
      description: 'Description of product 4',
    },
  ];

  const generateCatalogItems = () => {
    return products.map((product, index) => (
      <CatalogItem key={index}>
        <JewelryImage src={product.image} alt={`Product ${index + 1}`} />
        <Title>{product.title}</Title>
        <Price>{product.price}</Price>
        <Description>{product.description}</Description>
      </CatalogItem>
    ));
  };

  return (
    <CenteredContainer>
      <Container className="custom-container">
        <Title className="title">Catalogue</Title>
        <CatalogGrid>{generateCatalogItems()}</CatalogGrid>
      </Container>
    </CenteredContainer>
  );
};

export default Catalog;
