import React, { useState, useEffect } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";



const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Container = styled.div`
  text-align: center;
  opacity: ${({ animate }) => (animate ? 1 : 0)};
  transition: opacity 0.5s ease-in-out;
  animation: ${fadeIn} 1s ease-in-out;
`;

const Title = styled.h1`
  font-size: 28px;
  color: #333;
  margin-bottom: 20px;
  font-weight: bold;
  font-family: 'Georgia';
`;

const ContactInfo = styled.div`
  font-size: 16px;
  color: #777;
  margin-bottom: 20px;
`;

const Description = styled.div`
  font-size: 16px;
  color: #aaa;
  margin-bottom: 20px;
`;

const Accueil = () => {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);

    // Mettre à jour le titre de la page pour le référencement
    document.title = "ISS09 - Accueil";

    // Mettre à jour la balise meta pour la description
    const descriptionMeta = document.querySelector('meta[name="description"]');
    if (descriptionMeta) {
      descriptionMeta.setAttribute(
        "content",
        "Bienvenue chez Informatique, Services, Saverdun ! Découvrez notre boutique proposant une large gamme de produits informatiques et de services. Ouverts du mardi au samedi, de 9h30 à 12h30 et de 14h à 19h00."
      );
    }

    // Ajouter des liens internes
    const internalLinks = document.querySelectorAll('a[href^="/"]');
    internalLinks.forEach((link) => {
      link.setAttribute("rel", "internal");
    });
  }, []);

  return (
    <>
      <GlobalStyle />
      <Container className="custom-container" animate={animate}>
        <Title className="title">
          Bienvenue chez <bold>Informatique, Services, Saverdun</bold> !
        </Title>
        <ContactInfo>
          <Description>
          <span style={{ fontSize: "1.6em", textShadow: "1px 1px blue" }}>
            <strong><i></i></strong>
          </span>{" "}
          <span
            style={{
              fontSize: "1.2em",
              color: "grey",
              textShadow: "1px 1px black",
            }}
          >
            <p>
              Situé en plein cœur de Saverdun dans le 09, notre boutique vous
              propose une large gamme de produits informatiques et de services.
            </p>
          </span>
          <p>
            <strong>
              &#33; Nous sommes ouverts du mardi au samedi, de 9h30 à 12h30 et
              de 14h à 19h00. &#33;
            </strong>
          </p>
          <p>
            N'hésitez pas à nous rendre visite et découvrez notre sélection de
            produits de qualité.
          </p>
          </Description>
        </ContactInfo>
      </Container>
    </>
  );
};

export default Accueil;
