import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import logo from '../assets/logo.png';
import ContactModal from "./ContactModal"; // Assurez-vous de mettre le bon chemin

const Nav = styled.nav`
  background: linear-gradient(135deg, rgba(2,0,36,1) 0%, rgba(0,0,0,1) 89%, rgba(6,46,0,1) 100%, rgba(9,121,10,1) 100%);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 20px;
  border-bottom: 3px solid rgb(15,15,15);
  height: 55px;
`;

const Logo = styled.img`
  width: 50px;
  height: 50px;
  cursor: pointer;
`;

const Ul = styled.ul`
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
`;

const Li = styled.li`
  margin-left: 15px;
  position: relative;
  transition: background-color 0.3s ease-in-out;

  &:hover {
    background-color: rgba(0, 0, 0, 0.2);
    transition: opacity 0.3s ease-in-out, visibility 0.3s ease-in-out;
  }

  @media (max-width: 768px) {
    font-size: 1.8em;
    margin: 10px;
    transform: scale(1.2);
  }
`;

const StyledLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-weight: bold;
  font-size: 16px;
  letter-spacing: 1px;
  text-transform: uppercase;
  padding: 10px;

  &:hover {
    color: rgb(23, 101, 125);
  }
`;

const NavbarContainer = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 20px;
`;

const Navbar = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleLogoClick = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <NavbarContainer>
      <Nav>
        <Logo src={logo} alt="Logo" onClick={handleLogoClick} /> {/* Utilisez handleLogoClick ici */}
        <Ul>
          <Li>
            <StyledLink to="/">Accueil</StyledLink>
          </Li>
          <Li>
            <StyledLink to="/forfaits">Forfaits</StyledLink>
          </Li>
          <Li>
            <StyledLink to="/assistance">Assistance</StyledLink>
          </Li>
          <Li>
            <StyledLink to="/contact">Contact</StyledLink>
          </Li>
        </Ul>
      </Nav>
      {isModalOpen && <ContactModal onClose={handleCloseModal} />}
    </NavbarContainer>
  );
};

export default Navbar;
