import React, { useState, useEffect } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";
import upsLogo from "../assets/ups.webp";
import mondialRelayLogo from "../assets/mondialrelay.png";

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Black+Ops+One&display=swap');
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Container = styled.div`
  text-align: center;
  opacity: ${({ animate }) => (animate ? 1 : 0)};
  transition: opacity 0.5s ease-in-out;
  animation: ${fadeIn} 1s ease-in-out;
  margin-top: 30px;
  @media (max-width: 768px) {
    padding-bottom: 80px;
  }
`;

const Title = styled.h1``;

const ContactInfo = styled.div`
  font-size: 16px;
  color: #777;
  margin-bottom: 20px;
`;

const Description = styled.div`
  font-size: 16px;
  color: black;
  margin-bottom: 20px;
  width: 75%;
  margin: auto;
  margin-top: 0px;
`;

const StyledButton = styled.button`
  background: linear-gradient(135deg, #14453A 0%, #B19E63 100%);
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  cursor: pointer;
  margin: 10px;
  border: 1px solid grey;
  transition: background-color 0.3s ease-in-out, border-color 0.3s ease-in-out;

  &:hover {
    background: linear-gradient(135deg, #B19E63 0%, #14453A 100%);
    border-color: grey;
    color: black;
  }

  &:active {
    border-color: red;
  }

  @media (max-width: 768px) {
    line-height:45px;
    width: 100%;
  }

  @media (max-width: 1200px) {
    width: 100%;
  }
`;

const LogoContainer = styled.div`
  position: relative;
  margin-top: 20px;
  padding-bottom: 15px;

  @media (max-width: 768px) {
    padding-bottom: 100px;
    margin-left: 55px;
    margin-right: 55px;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
`;

const Logo = styled.img`
  max-width: 100px;
  height: auto;
  margin: 0 10px;
  position: absolute;
  bottom: 0;
  top: -40px;

  @media (max-width: 1200px) {
    top: 0;
    position: static;
    margin: 0px 0;
    margin-right: 25px;
    margin-left: 25px;
  }

  @media (max-width: 768px) {
    top: 0;
    position: static;
    margin: 10px 0;
  }
`;

const Accueil = () => {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);

    document.title = "Gemme le fil - Accueil";

    const descriptionMeta = document.querySelector('meta[name="description"]');
    if (descriptionMeta) {
      descriptionMeta.setAttribute(
        "content",
        "Bienvenue chez Gemme le fil ! Découvrez notre boutique d'artisanat de bijoux en macramé et pierres naturelles à Saverdun. Ouverts du mardi au samedi, de 9h30 à 12h30 et de 14h à 19h00."
      );
    }

    const internalLinks = document.querySelectorAll('a[href^="/"]');
    internalLinks.forEach((link) => {
      link.setAttribute("rel", "internal");
    });
  }, []);

  return (
    <>
      <GlobalStyle />
      <Container className="custom-container" animate={animate}>
        <Title>
          <strong className="title">Gemme le fil</strong>
        </Title>
        <ContactInfo>
          <Description>
            <span style={{ fontSize: "1.6em", textShadow: "1px 1px blue" }}>
              <strong>
                <i></i>
              </strong>
            </span>{" "}
            <span
              style={{
                fontSize: "1.2em",
                color: "black",
              }}
            >
              <p>
                Situé en plein cœur de Saverdun dans le 09, Gemme le fil est une
                boutique d'artisanat de bijoux en macramé et pierres naturelles.
              </p>
            </span>
            <p>
              <strong>
                &#33; Nous sommes ouverts du mardi au samedi, de 9h30 à 12h30 et
                de 14h à 19h00. &#33;
              </strong>
            </p>
            <p>
              N'hésitez pas à nous rendre visite et découvrez notre sélection de
              produits de qualité.
            </p>
            <p>
              Pour les nouveautés, présentation et autres{" "}
              <StyledButton
                as="a"
                href="https://www.facebook.com/gemmelefil"
                target="_blank"
                rel="noopener noreferrer"
              >
                lien Facebook
              </StyledButton>
              .
            </p>
          </Description>
        </ContactInfo>
        <LogoContainer>
          <Logo src={upsLogo} alt="Logo UPS" style={{ left: 0 }} />
          <Logo src={mondialRelayLogo} alt="Logo Mondial Relay" style={{ right: 0 }} />
        </LogoContainer>
        <Description>
          <p>
            Les livraisons sont suivies par UPS et Colis-privé. Faites confiance
            à nos partenaires pour des livraisons rapides et fiables.
          </p>
        </Description>
      </Container>
    </>
  );
};

export default Accueil;
