import React, { useEffect } from 'react';
import styled from 'styled-components';
import { generateRandomImageURL } from './utils'; // Fonction pour générer des URLs d'images aléatoires

const CenteredContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Container = styled.div`
  text-align: center;
  position: relative;
  transform: none;
  left: 0;
  top: 2050px;
`;

const Title = styled.h1`
  font-size: 28px;
  color: #333;
  margin-bottom: 20px;
`;

const CatalogGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const CatalogItem = styled.div`
  background: linear-gradient(135deg, #ffddb0 0%, #ffe1d8 100%);
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 2px 4px rgba(1, 34, 0, 0.5), 0 -2px 4px rgba(0, 0, 34, 0.5);
  text-align: center;
`;

const JewelryImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const Price = styled.p`
  font-size: 20px;
  color: grey;
  margin-bottom: 10px;
`;

const Description = styled.p`
  font-size: 16px;
  color: grey;
  margin-bottom: 20px;
`;

const Catalog = () => {
  useEffect(() => {
    document.title = 'Catalogue - ISS09 Informatiques saverdun';
  }, []);

  const generateCatalogItems = () => {
    const catalogItems = [];
    for (let i = 1; i <= 40; i++) {
      catalogItems.push(
        <CatalogItem key={i}>
          <JewelryImage src={generateRandomImageURL()} alt={`Jewelry ${i}`} />
          <Title>Jewelry {i}</Title>
          <Price>$99.99</Price>
          <Description>Beautiful jewelry item {i}</Description>
        </CatalogItem>
      );
    }
    return catalogItems;
  };

  return (
    <CenteredContainer>
      <Container className="custom-container">
        <Title className="title">Catalogue</Title>
        <CatalogGrid>{generateCatalogItems()}</CatalogGrid>
      </Container>
    </CenteredContainer>
  );
};

export default Catalog;
