import React, { useState, useEffect } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";
import upsLogo from "../assets/ups.webp"; // Import du logo UPS
import mondialRelayLogo from "../assets/mondialrelay.png"; // Import du logo Mondial Relay


const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Black+Ops+One&display=swap');
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Container = styled.div`
  text-align: center;
  opacity: ${({ animate }) => (animate ? 1 : 0)};
  transition: opacity 0.5s ease-in-out;
  animation: ${fadeIn} 1s ease-in-out;
`;

const Title = styled.h1`
  font-size: 28px;
  color: #14453A; /* Vert */
  margin-bottom: 20px;
  font-weight: bold;
  border: 1px solid black;
  font-family: ' brush script mt'; /* Utilisation de la police personnalisée */
  font-style: italic;
`;

const ContactInfo = styled.div`
  font-size: 16px;
  color: #777;
  margin-bottom: 20px;
`;

const Description = styled.div`
  font-size: 16px;
  color: black;
  margin-bottom: 20px;
`;

const LogoContainer = styled.div`
  position: relative;
  margin-top: 20px;
`;

const Logo = styled.img`
  max-width: 100px;
  height: auto;
  margin: 0 10px;
  position: relative;
  bottom: 0;

`;

const Accueil = () => {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);

    // Mettre à jour le titre de la page pour le référencement
    document.title = "Gemme le fil - Accueil";

    // Mettre à jour la balise meta pour la description
    const descriptionMeta = document.querySelector('meta[name="description"]');
    if (descriptionMeta) {
      descriptionMeta.setAttribute(
        "content",
        "Bienvenue chez Gemme le fil ! Découvrez notre boutique d'artisanat de bijoux en macramé et pierres naturelles à Saverdun. Ouverts du mardi au samedi, de 9h30 à 12h30 et de 14h à 19h00."
      );
    }

    // Ajouter des liens internes
    const internalLinks = document.querySelectorAll('a[href^="/"]');
    internalLinks.forEach((link) => {
      link.setAttribute("rel", "internal");
    });
  }, []);

  return (
    <>
      <GlobalStyle />
      <Container className="custom-container" animate={animate}>
        <Title className="title">
          <strong>Gemme le fil</strong>
        </Title>
        <ContactInfo>
          <Description>
            <span style={{ fontSize: "1.6em", textShadow: "1px 1px blue" }}>
              <strong><i></i></strong>
            </span>{" "}
            <span
              style={{
                fontSize: "1.2em",
                color: "black",
              }}
            >
              <p>
                Situé en plein cœur de Saverdun dans le 09, Gemme le fil est une boutique d'artisanat de bijoux en macramé et pierres naturelles.
              </p>
            </span>
            <p>
              <strong>
                &#33; Nous sommes ouverts du mardi au samedi, de 9h30 à 12h30 et
                de 14h à 19h00. &#33;
              </strong>
            </p>
            <p>
              N'hésitez pas à nous rendre visite et découvrez notre sélection de
              produits de qualité.
            </p>
          </Description>
        </ContactInfo>
      </Container>
      <Container className="custom-container" animate={animate}>
        <LogoContainer>
          <Logo src={upsLogo} alt="Logo UPS" style={{ left: 0 }} />
          <Logo src={mondialRelayLogo} alt="Logo Mondial Relay" style={{ right: 0 }} />
        </LogoContainer>
        <Description>
          <p>
            Les livraisons sont suivies par UPS et Colis-privé. Faites confiance
            à nos partenaires pour des livraisons rapides et fiables.
          </p>
        </Description>
      </Container>
    </>
  );
};

export default Accueil;
