import React, { useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  text-align: center;
  width: 95vw;
  transform: none;
  position: absolute;
  top: 125px;
  left: 50%;
  transform: translateX(-50%);
`;

const Title = styled.h1`
  font-size: 2.2em;
  color: #14453A;
  margin-bottom: 20px;
  border: 1px solid black;
  font-family: 'brush script mt';
  font-style: italic;
`;

const CatalogGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
  margin-bottom: 50px;
  padding: 0 20px;
`;

const CatalogItem = styled.div`
  background: linear-gradient(
    135deg,
    #14453A 0%,
    #B19E63 100%
  );

  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 2px 4px rgba(1, 34, 0, 0.5), 0 -2px 4px rgba(0, 0, 34, 0.5);
  text-align: center;
`;

const JewelryImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const Price = styled.p`
  font-size: 20px;
  color: #14453A;
  margin-bottom: 10px;
`;

const Description = styled.p`
  font-size: 16px;
  color: #333;
  margin-bottom: 20px;
`;

const Catalog = () => {
  useEffect(() => {
    document.title = 'Catalogue - Gemme le fil';
  }, []);

  const products = [
    {
      image: require('../assets/Bijoux/Boucles d\'oreilles agate bleue 24 €.jpg').default,
      title: 'Boucles d\'oreilles Agate Bleue',
      price: '24 €',
      description: 'Des belles boucles d\'oreilles en agate bleue pour illuminer votre journée.',
    },
    {
      image: require('../assets/Bijoux/Boucles d\'oreilles aigue marine 18 €.jpg').default,
      title: 'Boucles d\'oreilles Aigue Marine',
      price: '18 €',
      description: 'Des boucles d\'oreilles élégantes avec des pierres d\'aigue marine.',
    },
    {
      image: require('../assets/Bijoux/Boucles d\'oreilles apatite 12 €.JPG').default,
      title: 'Boucles d\'oreilles Apatite',
      price: '12 €',
      description: 'De magnifiques boucles d\'oreilles en apatite à un prix incroyable.',
    },
    // Ajoutez les autres produits de la même manière
    {
      image: require('../assets/Bijoux/Boucles d\'oreilles sablier 18 €.jpg').default,
      title: 'Boucles d\'oreilles Sablier',
      price: '18 €',
      description: 'Des boucles d\'oreilles uniques avec un design de sablier.',
    },
    {
      image: require('../assets/Bijoux/Boucles d\'oreilles turquoise 24 €.jpg').default,
      title: 'Boucles d\'oreilles Turquoise',
      price: '24 €',
      description: 'Des boucles d\'oreilles en turquoise pour un look bohème.',
    },
    {
      image: require('../assets/Bijoux/Collier angélique 42 €.JPG').default,
      title: 'Collier Angélique',
      price: '42 €',
      description: 'Un collier angélique qui apporte de la grâce à votre tenue.',
    },
    {
      image: require('../assets/Bijoux/Collier apatie 39 €.png').default,
      title: 'Collier Apatie',
      price: '39 €',
      description: 'Un collier élégant avec des pierres d\'apatite.',
    },
    {
      image: require('../assets/Bijoux/Collier donut et macramé 41 €.jpg').default,
      title: 'Collier Donut et Macramé',
      price: '41 €',
      description: 'Un collier unique avec un donut en pierre et une touche de macramé.',
    },
    {
      image: require('../assets/Bijoux/Collier écusson et jaspe paysage 34 €.JPG').default,
      title: 'Collier Écusson et Jaspe Paysage',
      price: '34 €',
      description: 'Un collier avec un écusson en jaspe paysage pour un look terreux.',
    },
    {
      image: require('../assets/Bijoux/Collier Lapis Lazuli 49 €.JPG').default,
      title: 'Collier Lapis Lazuli',
      price: '49 €',
      description: 'Un collier en lapis lazuli pour apporter une touche de bleu à votre style.',
    },
    {
      image: require('../assets/Bijoux/Collier malachite et pétales 39 €.png').default,
      title: 'Collier Malachite et Pétales',
      price: '39 €',
      description: 'Un collier avec des pierres de malachite et des détails de pétales.',
    },
    {
      image: require('../assets/Bijoux/Collier Mandala et estampe 31 €.JPG').default,
      title: 'Collier Mandala et Estampe',
      price: '31 €',
      description: 'Un collier avec un mandala et une estampe pour une touche artistique.',
    },
    {
      image: require('../assets/Bijoux/Collier sodalite 45 €.jpg').default,
      title: 'Collier Sodalite',
      price: '45 €',
      description: 'Un collier en sodalite pour un style unique et élégant.',
    },
    {
      image: require('../assets/Bijoux/Manchette apatie 37 €.JPG').default,
      title: 'Manchette Apatie',
      price: '37 €',
      description: 'Une manchette impressionnante avec des pierres d\'apatite.',
    },
  ];

  const generateCatalogItems = () => {
    return products.map((product, index) => (
      <CatalogItem key={index}>
        <JewelryImage src={product.image} alt={product.title} />
        <Title>{product.title}</Title>
        <Price>{product.price}</Price>
        <Description>{product.description}</Description>
      </CatalogItem>
    ));
  };

  return (
    <Container className="custom-container">
      <Title className="title">Réalisations maison</Title>
      <CatalogGrid>{generateCatalogItems()}</CatalogGrid>
    </Container>
  );
};

export default Catalog;
