import React from "react";
import styled from "styled-components";

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7); /* Fond plus opaque */
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000; /* Met le modal au premier plan */
`;

const ContactCard = styled.div`
  background-color: black; /* Fond noir */
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  max-width: 400px;
  width: 100%;
  color: white; /* Texte en blanc */
  text-align: center; /* Centrer le texte */
  position: relative; /* Ajout de position relative pour le bouton de fermeture */
  border: 5px solid darkgreen;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background-color: transparent;
  border: none;
  font-size: 30px; /* Croix plus grosse */
  color: white; /* Croix en blanc */
  cursor: pointer;

  &:hover{
    color: green;
  }
`;

const Map = styled.iframe`
  width: 100%;
  height: 300px;
  border: 0;
`;

const StyledButton = styled.button`
  background: linear-gradient(135deg, rgba(33, 255, 0, 1) 0%, rgb(6, 70, 7) 0%, rgba(0, 0, 0, 1) 72%, rgba(2, 0, 36, 1) 100%);
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  cursor: pointer;
  margin: 10px;
  border: 1px solid grey;
  transition: background-color 0.3s ease-in-out, border-color 0.3s ease-in-out;

  &:hover {
    background: linear-gradient(135deg, rgba(0, 0, 0, 1) 0%, rgb(6, 70, 7) 0%, rgba(33, 255, 0, 1) 72%, rgba(2, 0, 36, 1) 100%);
    border-color: grey;
    color: black;
  }

  &:active {
    border-color: red;
  }
`;

const ContactModal = ({ onClose }) => {
  return (
    <ModalOverlay>
      <ContactCard>
        <CloseButton onClick={onClose}>×</CloseButton>
        <h2>Informations de contact</h2>
        <p>
          Informatique, Services, Saverdun. <br />
          Nous sommes ouverts du mardi au samedi, de 9h30 à 12h30 et de 14h à 19h00.
        </p>
        <p>Par Mail : iss09@free.fr</p>
        <p>Au téléphone : 06 51 09 26 11</p>
        <Map
          src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2906.7623341363687!2d1.5730072154842643!3d43.235442379137886!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12aee14e1cce7f8b%3A0xfcd49e3f5b7e0778!2sISS09!5e0!3m2!1sfr!2sfr!4v1626419896001!5m2!1sfr!2sfr"
          allowFullScreen=""
          loading="lazy"
          title="Google Maps"
        ></Map>
        {/* Boutons modernes */}
        <StyledButton onClick={() => window.print()}>Imprimer</StyledButton>
        <StyledButton onClick={onClose}>Fermer</StyledButton>
      </ContactCard>
    </ModalOverlay>
  );
};

export default ContactModal;
