import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import logo from "../assets/logo.png";

const initialRotateAnimation = keyframes`
  0% { transform: rotateY(0deg) scale(0.6); }
  30% { transform: rotateY(360deg) scale(1.2); }
  100% { transform: rotateY(360deg) scale(1); }
`;

const collisionAnimation = keyframes`
  0% { transform: translateX(0) translateY(0) rotate(0deg); }
  30% { transform: translateX(50px) translateY(-50px) rotate(45deg); }
  60% { transform: translateX(-50px) translateY(50px) rotate(-45deg); }
  100% { transform: translateX(0) translateY(0) rotate(0deg); }
`;

const fadeInAnimation = keyframes`
  0% { opacity: 0; }
  100% { opacity: 1; }
`;

const AnimationContainer = styled.div`
  background-color: black;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const LogoContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  animation: ${initialRotateAnimation} 5s ease-in-out infinite;
  transform-origin: center center;
  image-rendering: auto;
`;

const Logo = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
  opacity: 1;
`;

const TextContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  animation: ${fadeInAnimation} 1s ease-in-out 6s forwards;
  margin-left: 50px;
`;

const Title = styled.h1`
  font-size: 48px;
  color: white;
  margin-bottom: 20px;
  opacity: 0;
  animation: ${fadeInAnimation} 1s ease-in-out 1s forwards;
  text-align: left;
  text-shadow: 4px 4px 20px darkblue;
`;

const Subtitle = styled.h2`
  font-size: 24px;
  color: white;
  opacity: 0;
  animation: ${fadeInAnimation} 1s ease-in-out 2s forwards;
  text-align: left;
  text-shadow: 4px 4px 20px darkblue;
`;

const IntroAnimation = ({ onAnimationComplete }) => {
  const [animateLogo, setAnimateLogo] = useState(true);
  const [animateText, setAnimateText] = useState(false);

  useEffect(() => {
    const animationDuration = 5000;

    const timer1 = setTimeout(() => {
      setAnimateLogo(false);
      setAnimateText(true);
      if (typeof onAnimationComplete === "function") {
        onAnimationComplete();
      }
    }, animationDuration);

    return () => {
      clearTimeout(timer1);
    };
  }, [onAnimationComplete]);

  return (
    <AnimationContainer>
      <LogoContainer>
        <Logo src={logo} alt="Logo" />
      </LogoContainer>
      <TextContainer>
        <Title>Bienvenue chez</Title>
        <Subtitle>Informatique, Services, Saverdun !</Subtitle>
      </TextContainer>
    </AnimationContainer>
  );
};

export default IntroAnimation;
